package online_class;
import java.util.*;
public class PrintEven {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner sc=new Scanner(System.in);
		System.out.println("enter term");
		int n=sc.nextInt();
		while(n>1) {
			System.out.println(n);
			n-=2;
		}

	}

}
